public class RectanglePerimeter {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int length, width, perimeter;
		
		width = 4;
		length = 13;
		perimeter = (2 * width) + (2 * length);
		
		System.out.print("Perimeter of rectangle: " + perimeter);
	}

}
